import React from 'react';
import { useState } from 'react';

const NewsItem = (props) => {
    const { title, description, imageUrl, newsUrl, author, date, source } = props;
    const [isBookmarked, setIsBookmarked] = useState(false);

    const toggleBookmark = () => {
        setIsBookmarked(!isBookmarked);
        // Add logic to add/remove the article from bookmarks
    };

    return (
        <div className="my-3 position-relative">
            {isBookmarked && (
                <div style={{ position: 'absolute', bottom: '5px', right: '5px', zIndex: '1' }}>
                    {/* Use the SVG code directly */}
                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="16"
                        height="16"
                        fill="currentColor"
                        className="bi bi-bookmarks-fill text-danger"
                        viewBox="0 0 16 16"
                    >
                        <path
                            fillRule="evenodd"
                            d="M2 4a2 2 0 0 1 2-2h6a2 2 0 0 1 2 2v11.5a.5.5 0 0 1-.777.416L7 13.101l-4.223 2.815A.5.5 0 0 1 2 15.5zm2-1a1 1 0 0 0-1 1v10.566l3.723-2.482a.5.5 0 0 1 .554 0L11 14.566V4a1 1 0 0 0-1-1z"
                        />
                        <path
                            fillRule="evenodd"
                            d="M4.268 1H12a1 1 0 0 1 1 1v11.768l.223.148A.5.5 0 0 0 14 13.5V2a2 2 0 0 0-2-2H6a2 2 0 0 0-1.732 1"
                        />
                    </svg>
                </div>
            )}
            <div className="card">
                {/* Card content */}
                <div className="card-body">
                    {/* Card body content */}
                    <button className="btn btn-outline-secondary" onClick={toggleBookmark}>
                        {/* Bookmark button content */}
                        {isBookmarked ? (
                            <svg
                                xmlns="http://www.w3.org/2000/svg"
                                width="16"
                                height="16"
                                fill="currentColor"
                                className="bi bi-bookmarks-fill text-danger"
                                viewBox="0 0 16 16"
                            >
                                <path
                                    fillRule="evenodd"
                                    d="M2 4a2 2 0 0 1 2-2h6a2 2 0 0 1 2 2v11.5a.5.5 0 0 1-.777.416L7 13.101l-4.223 2.815A.5.5 0 0 1 2 15.5zm2-1a1 1 0 0 0-1 1v10.566l3.723-2.482a.5.5 0 0 1 .554 0L11 14.566V4a1 1 0 0 0-1-1z"
                                />
                                <path
                                    fillRule="evenodd"
                                    d="M4.268 1H12a1 1 0 0 1 1 1v11.768l.223.148A.5.5 0 0 0 14 13.5V2a2 2 0 0 0-2-2H6a2 2 0 0 0-1.732 1"
                                />
                            </svg>
                        ) : (
                            <svg
                                xmlns="http://www.w3.org/2000/svg"
                                width="16"
                                height="16"
                                fill="currentColor"
                                className="bi bi-bookmark text-secondary"
                                viewBox="0 0 16 16"
                            >
                                <path
                                    fillRule="evenodd"
                                    d="M2 1a1 1 0 0 1 1-1h10a1 1 0 0 1 1 1v14l-5-3-5 3V1zm4.5 4a.5.5 0 0 0-.5.5v2.634l-1.618-1.054a.5.5 0 1 0-.5.866l2.118 1.382a.5.5 0 0 0 .618 0l2.118-1.382a.5.5 0 1 0-.5-.866L6 8.134V5.5a.5.5 0 0 0-.5-.5z"
                                />
                            </svg>
                        )}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default NewsItem;
