import React from 'react';
import { Link } from 'react-router-dom';

const NavBar = (props) => {
    const uniqueId = props.uniqueId || 'default'; // Use 'default' if no uniqueId is provided
    const switchId = `flexSwitchCheckDefault-${uniqueId}`;

    return (
        <div>
            <nav className="navbar navbar fixed-top navbar-expand-lg bg-body-tertiary">
                <div className="container-fluid">
                    <Link className="navbar-brand" to="/">NewsMine</Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link" aria-current="page" to="/">Home</Link>
                            </li>
                            {/* Other list items */}
                        </ul>
                        <div className="d-flex align-items-center">
                            <button className="btn btn-secondary me-2" onClick={props.toggleShowBookmarks}>
                                {props.showBookmarks ? 'View News Feed' : 'View Bookmarks'}
                            </button>
                            <div className="form-check form-switch">
                                <input className="form-check-input" onClick={props.togglestyle} type="checkbox" role="switch" id={switchId} />
                                <label className="form-check-label" htmlFor={switchId}>{props.mode === 'light' ? 'Light Mode' : 'Dark Mode'}</label>
                            </div>
                        </div>
                    </div>
                </div>
            </nav>
        </div>
    );
};

export default NavBar;
